import React, { useState } from 'react';
import WeatherInfo from './WeatherInfo';
import PlacesInfo from './PlacesInfo';

function LocationInfo({ locations }) {
    const [weatherData, setWeatherData] = useState(null);
    const [placesData, setPlacesData] = useState(null);
    const [loadingWeather, setLoadingWeather] = useState(false);
    const [loadingPlaces, setLoadingPlaces] = useState(false);
    const [error, setError] = useState(null);

    const fetchWeatherData = async (lat, lng) => {
        if (locationData) {
          setLoadingWeather(true);
          setError(null);
          try {
            const response = await fetch(`/app/weather?lat=${lat}&lon=${lng}`);
            if (!response.ok) {
              throw new Error('Network response was not ok');
            }
            const data = await response.json();
            setWeatherData(data);
          } catch (error) {
            console.error('Error fetching weather data:', error);
            setError('Failed to fetch weather data. Please try again.');
          } finally {
            setLoadingWeather(false);
          }
        }
    };

    const fetchPlacesData = async (lat, lng) => {
          if (locationData) {
            setLoadingPlaces(true);
            setError(null);
            try {
              const response = await fetch(`/app/places?lat=${lat}&lon=${lng}`);
              if (!response.ok) {
                throw new Error('Network response was not ok');
              }
              const data = await response.json();
              setPlacesData(data);
            } catch (error) {
              console.error('Error fetching weather data:', error);
              setError('Failed to fetch weather data. Please try again.');
            } finally {
              setLoadingPlaces(false);
            }
          }
      };

      return (
        <div className="location-info">
        <h2>Locations</h2>
           <ul>
           {locations.map((location) => (
                <li key={location.osm_id}>
                    <h3>Location: {location.name} {location.country}</h3>
                    <p>Широта: {location.point.lat}</p>
                    <p>Долгота: {location.point.lng}</p>
                    <button onClick={() => fetchWeatherData(location.point.lat, location.point.lng)}>Показать погоду</button>
                    {loadingWeather && <p>Loading...</p>}
                    {weatherData && <WeatherInfo weather={weatherData} />}
                    <button onClick={() => fetchPlacesData(location.point.lat, location.point.lng)}>Показать место</button>
                    {loadingPlaces && <p>Loading...</p>}
                    {placesData && <PlacesInfo places={placesData} />}
                </li>
           ))}
           </ul>
           {error && <p style={{ color: 'red' }}>{error}</p>}
        </div>
      );
}

export default LocationInfo;
